import React, { Component } from 'react';
const productConsumer = React.createContext();

class DataProvider extends Component {
    state = {}
    render() {
        return ( <
            productConsumer.Provider value = "hello bhi je" > { this.props.children } <
            /productConsumer.Provider>
        );
    }
}
const product = productConsumer.Consumer;
export { DataProvider, product }
