import React, { Component } from "react";
import Product from "./product";
import { Jumbotron, Container, Row, Col, Button, Image,Card } from "react-bootstrap";
import { storeProducts } from "../data";
import styled from "styled-components";
import { ProductConsumer } from "../context";

export default class ProductList extends Component {
    state = {
        products: storeProducts
    };
    card() {
        const element = (
          <Card style={{ height: '30em' }}>
            <Card.Body>
              <Card.Header>Filter Items
</Card.Header>
              <Card.Text>
                <p>Category: Regional
Department: French</p>
              </Card.Text>

            </Card.Body>
          </Card>)
        return element;

      }
    render() {
        return (
            <React.Fragment>

                <div className="py-5">
                    <div className="container">
                    <Row className="text-center">
                        <Col className="col-9 mx-auto col-md-6 col-lg-3 my-3" xs={2} md={2}>
                        {this.card()}
                        </Col>
                             <Col  xs={9} md={9}>
                            <Row>
                            <ProductConsumer>
                              { (name)=>{
                              return name.productOfshop.map(products=>{
                                   return <Product products={products} key={products.id}/>
                               })
                              console.log({name})
                              }}
                            </ProductConsumer>
                            </Row>

                        </Col>
                    </Row>
                </div>
                </div>
            </React.Fragment>
        );
    }
}

const ProductWrapper = styled.section``;
